import React, { Fragment, useState } from 'react';


const InputTask = () => {
const [username, setUsername] = useState("");
const [phone, setPhone] = useState("");
const [email, setEmail] = useState("");
const [date_of_creating, setDate] = useState("");

    return (
        <Fragment>
            <h1 className="text-center mt-5">Pern TasksList</h1>
            <form className="d-flex mt-5">
                <label> User Name
                  <input type="text" className="form-control" name="userName"
                    id="username" value={username} onChange={e => setUsername(e.target.value)} />
                </label> <br/>
                <label> Phone
                  <input type="tel" className="form-control"  
                    name="phone" id="phone" value={phone} />
                </label>
                <label> email
                  <input type="email" className="form-control"  
                    name="email" id="email" value={email} />
                </label>
                <label> Date
                  <input type="date" className="form-control"  
                    name="date" id="date" value={date_of_creating} />
                </label>
                <br/>
                <button className="btn btn-success">Add</button>
            </form>
        </Fragment>
    );
};

export default InputTask;
